mapper.select:
select ID, BUSI_DATE, STAFF_CODE, STAFF_NAME, DEPART_CODE, UNITS_DEPART, STAFF_POSITION, STAFF_JOB, STAFF_MODULE, PHONE, MAIL, PERMISSION_CHANGE, APPLY_DATE, ACCOUNT_DELETE_REASON, NOTE, CONFIRM_STATE from
where STATE=1
and USER_DEPART = #{pd.SelectedDepartCode}
and CONFIRM_STATE IN (${pd.confirmState})
and BUSI_DATE = #{pd.busiDate}
and USER_DEPART = #{pd.USER_DEPART}
order by CONFIRM_STATE ASC

