mapper.select:
SELECT a.TEST_PLAN_ID, a.TEST_PAPER_ID, a.START_DATE, a.END_DATE, a.TEST_PLAN_PERSONS, b.TEST_PAPER_TITLE, b.COURSE_TYPE_ID, b.TEST_PAPER_TYPE, b.TEST_PAPER_DIFFICULTY, b.TEST_QUESTION_NUM, b.TEST_PAPER_SCORE, b.ANSWER_TIME, b.QUALIFIED_SCORE FROM tb_test_plan AS a LEFT JOIN tb_test_paper AS b ON a.TEST_PAPER_ID = b.TEST_PAPER_ID WHERE a.STATE = 1 AND b.STATE = 1
AND ( b.TEST_PAPER_TITLE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)

select b.USERNAME, a.TEST_PLAN_ID, a.TEST_PAPER_ID, a.TEST_USER, a.TEST_TIME, a.TEST_COUNT, a.TEST_SCORE, a.IF_QUALIFIED, a.TEST_QUESTION_NUM, a.STATE, a.CREATE_DATE from tb_test_main as a LEFT JOIN sys_user AS b ON a.TEST_USER = b.USER_ID where a.STATE=1
and ( b.USERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)

		SELECT * from tb_test_main		
 		WHERE 
 			TEST_PLAN_ID=#{TEST_PLAN_ID}
 		AND 
 			TEST_PAPER_ID=#{TEST_PAPER_ID}
		AND
			TEST_USER=#{TEST_USER}
