mapper.update:
update
set PRO_CODE = #{PRO_CODE}, PRO_TITLE = #{PRO_TITLE}, PRO_REPORT_USER = #{PRO_REPORT_USER}, PRO_ACCEPT_USER = #{PRO_ACCEPT_USER}, PRO_DEPART = #{PRO_DEPART}, PRO_SYS_TYPE = #{PRO_SYS_TYPE}, PRO_TYPE_ID = #{PRO_TYPE_ID}, PRO_TAG = #{PRO_TAG}, PRO_PRIORITY = #{PRO_PRIORITY}, PRO_RESOLVE_TIME = #{PRO_RESOLVE_TIME}, PRO_CONTENT = #{PRO_CONTENT},
UPDATE_DATE = #{UPDATE_DATE}, CUST1 = #{CUST1}, CUST2 = #{CUST2}, CUST3 = #{CUST3}, CUST4 = #{CUST4}, CUST5 = #{CUST5}, CUST6 = #{CUST6} where PRO_CODE = #{PRO_CODE}

mapper.select:
SELECT A.*, depart.NAME PRO_DEPART_NAME, problemType.PRO_TYPE_NAME, dic.NAME PRO_SYS_TYPE_NAME FROM tb_problem_info A
LEFT JOIN oa_department depart ON A.PRO_DEPART=depart.DEPARTMENT_CODE
LEFT JOIN tb_problem_type problemType ON A.PRO_TYPE_ID=problemType.PRO_TYPE_ID
LEFT JOIN (SELECT DICT_CODE,NAME FROM sys_dictionaries WHERE PARENT_CODE = 'SYSTEM' ) dic ON A.PRO_SYS_TYPE = dic.DICT_CODE
${pd.PRO_ACCESS}
and (
PRO_TITLE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
AND A.PRO_STATE = #{pd.PRO_STATE}
ORDER BY pro_code DESC

		select

		from 




				and (
					CATEGORYNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
					or
					COUNTRY LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
					)




				and
					(
					${pd.searchField} LIKE CONCAT(CONCAT('%', #{pd.searchString}),'%')
					)


				and
					(
					${pd.searchField} = #{pd.searchString}
					)


				and
					(
					${pd.searchField} != #{pd.searchString}
					)



		order by CATEGORYNAME ${pd.sord} 

              limit #{currentResult}, #{rowNum}

