mapper.select:
select
1
from

where
DICT_CODE = #{DICT_CODE}

	and DICTIONARIES_ID != #{DICTIONARIES_ID}

select A.NAME,A.NAME_EN,A.DICT_CODE,A.ORDER_BY,A.PARENT_CODE,A.BZ,A.TBSNAME,A.DICTIONARIES_ID,IFNULL(B.NAME,'所有字典') AS PARENT_NAME from
A LEFT JOIN
B ON A.PARENT_CODE=B.DICT_CODE
and A.PARENT_CODE = #{pd.DICT_CODE}
and ( A.NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or A.NAME_EN LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or A.DICT_CODE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
${pd.filterWhereResult}
order by A.PARENT_CODE
,${sidx} ${sord}
limit #{currentResult}, #{rowNum}

SELECT * FROM( select A.NAME,A.NAME_EN,A.DICT_CODE,A.ORDER_BY,A.PARENT_CODE,A.BZ, A.DICTIONARIES_ID,IFNULL(B.NAME,'所有字典') AS PARENT_NAME from
A LEFT JOIN
B ON A.PARENT_CODE=B.DICT_CODE
A.PARENT_CODE!='0' AND B.DICT_CODE!='FMISACC' AND B.PARENT_CODE!='FMISACC'
UNION SELECT NAME,NAME_EN,DEPARTMENT_CODE AS DICT_CODE,'' AS ORDER_BY,'oa_department' AS PARENT_CODE,BZ,DEPARTMENT_ID AS DICTIONARIES_ID,'组织机构字典' AS PARENT_NAME FROM oa_department
order by PARENT_CODE
,${sidx} ${sord}
) TEMP
and PARENT_CODE = #{DICT_CODE}
and ( NAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') or NAME_EN LIKE CONCAT(CONCAT('%', #{keywords}),'%') or DICT_CODE LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
${filterWhereResult}

select count(*) from
and PARENT_CODE = #{DICT_CODE}
and ( NAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') or NAME_EN LIKE CONCAT(CONCAT('%', #{keywords}),'%') or DICT_CODE LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
${filterWhereResult}

select
count(*) zs
from
${thisTable}
