mapper.select:
SELECT a.RPT_DUR,
a.TABLE_CODE, a.COL_CODE, a.COL_NAME, a.DISP_ORDER, a.DICT_TRANS, a.COL_HIDE, a.COL_SUM, a.COL_AVE,
a.COL_FORMULA, a.CAL_ORDER, a.NUM_DGT, a.DEC_PRECISION,
c.KPI_NAME TABLE_NAME, d.DICT_NAME FROM tb_tmpl_config_detail a
LEFT JOIN tb_assess_kpi c on a.TABLE_CODE = c.TABLE_CODE LEFT JOIN tb_tmpl_config_dict d on a.DICT_TRANS = d.DICT_CODE
and a.TABLE_CODE = #{TABLE_CODE}
${filterWhereResult}
order by ${sidx} ${sord}

select * from TB_TMPL_CONFIG_BASE
${pd.QueryFeild}
order by TABLE_NO

