mapper.delete:
delete from

where 
	USERNAME = #{USERNAME}
	AND
		MODULE = #{MODULE}

mapper.update:
update
set USERNAME=#{USERNAME}, NAME1=#{NAME1}, UNIT_CODE=#{UNIT_CODE}, TEST_SCORE=#{TEST_SCORE}, MODULE=#{MODULE}, TEST_NAME=#{TEST_NAME}, TEST_DATE=#{TEST_DATE}, IF_QUALIFIED=#{IF_QUALIFIED}, CREAT_USER=#{CREAT_USER}, CREAT_DATE=#{CREAT_DATE}, QUALIFIED_SCORE=#{QUALIFIED_SCORE}, STATE=#{STATE}
where USERNAME = #{USERNAME} AND MODULE = #{MODULE}

mapper.select:
select 

from 

where 
	USERNAME = #{USERNAME}
	AND
		MODULE = #{MODULE}

select A.*,unit.NAME UNIT_NAME,user.NAME USER_NAME from A LEFT JOIN sys_user user ON A.USERNAME=user.USERNAME LEFT JOIN oa_department unit ON A.UNIT_CODE=unit.DEPARTMENT_CODE
and ( A.NAME1 LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or unit.NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or A.TEST_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)

select * 
from 





		and UNIT_CODE = #{pd.UNIT_CODE}


		and MODULE = #{pd.MODULE}


		and TEST_DATE >= #{pd.start}


		and TEST_DATE <= #{pd.end}


order by MODULE,  UNIT_CODE
